mapper.select:

    	select * from  t_product_detail_image 
    	where fileType='detail_image'
		and product_id=#{product_id}


    	select * from  t_product_detail_image 
    	where fileType='price_image'
		and product_id=#{product_id}


    	select * from  t_product_detail_image 
    	where fileType='facility_image'
		and product_id=#{product_id}

select t_product.* , t_product_detail_image.fileName from t_product, t_product_detail_image where t_product.product_id=t_product_detail_image.product_id and t_product_detail_image.filetype='main_image' and t_product.center_name like concat('%',#{searchWord},'%')
and t_product.product_price between #{minPrice} and #{maxPrice}
and t_product.product_price >= #{minPrice}
and #{maxPrice} >= t_product.product_price
order by t_product.product_entered_date desc
